			// click(PWALiveTVPage.objFirstPremiumCardinTray, "Premium Content");
			waitTime(5000);
			// waitForElementDisplayed(PWASubscriptionPages.objSubscribePopupTitle, 5);
			waitForElementDisplayed(PWAPlayerPage.objGetPremiumInline, 5);
