	public By TextToXpathusingclass(String text) throws Exception {
		return By.xpath("//div[@class='" + text + "']");
	}

	public By TextToXpathusingclasswithindex(String text, int i) throws Exception {
		return By.xpath("(//*[@class='" + text + "'])[" + i + "]");
	}


